// useStateÎäî const [Î≥ÄÏàòÍ∞í Í∞ÄÏ†∏Ïò§Í∏∞,Î≥ÄÏàòÍ∞í ÏÑ§Ï†ïÌïòÍ∏∞] = useState(Ï¥àÍ∏∞Í∞í); Î°ú ÏûÖÎ†•Ìï®
  // üòÄ useStateÎäî ÌéòÏù¥ÏßÄ ÎûúÎçîÎßÅ Ïãú 1ÌöåÎßå! Î∞úÎèô! Ïù¥ÌõÑ Î∞úÎèô ÌïòÏßÄ ÏïäÎäîÎã§!!
  const [inputValue,setInputValue] = useState('');    // ÏïÑÏßÅ ÏûÖÎ†•Ìïú Í∞íÏùÄ Îπà Í∞íÏù¥ÎãàÍπå ÎπÑÏõåÎëêÍ∏∞
  const [todoList,setTodoList] = useState([]);        // ÏïÑÏù¥ÌÖúÏùÑ Î™®ÏïÑÎëêÎ†§Î©¥ Ïñ¥Îñ§ ÌÉÄÏûÖÏù¥ Ï†úÏùº Ï¢ãÏúºÎÉê Î∞∞Ïó¥Ïù¥ Ï¢ãÎã§.

  const addItem = () => {
    console.log("inputValueÍ∞íÏùÑ todoListÏóê Ï∂îÍ∞Ä!");
    setTodoList([...todoList, inputValue])  // Í∏∞Ï°¥Ïóê ÏûàÎäî ÏïÑÏù¥ÌÖú Ïú†ÏßÄÌïòÍ≥† ÏÉàÎ°úÏ∂îÍ∞Ä
  }

  const DeleteClick = (delete_item) => {
    console.log("ÌïÑÌÑ∞ ÎêòÍ∏∞Ï†Ñ todoList :" + todoList)
    let result = todoList.filter(item=>item!==delete_item);   // Ï°∞Í±¥Ïóê ÎßåÏ°±ÌïòÎäî Ïï†Îì§Îßå Î™®ÏïÑÏÑú Î∞∞Ïó¥ÎßåÎì§Ïñ¥Îùº!
    // var result = if(1==1) "ÍπÄÏ£ºÌù¨";
    // var result = if(1==1) {"ÍπÄÏ£ºÌù¨";} 
    console.log("ÌïÑÌÑ∞ Îêú ÌõÑ todoList : " + result);

    setTodoList(result);
    console.log(delete_item)
    console.log("ÏÇ≠Ï†úÏ§ë...")
  }


  return (
    <main>
      <input type="text" onChange={(ÌÖçÏä§Ìä∏input)=>{setInputValue(ÌÖçÏä§Ìä∏input.target.value); console.log("inputValueÍ∞í Î≥ÄÍ≤Ω!")}}></input>
      <button onClick={addItem}>Ï∂îÍ∞Ä</button>
      <TodoBoard delete={DeleteClick} todoList={todoList}/>
    </main>
  );
}

export default App;
