import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import TodoBoard from './components/TodoBoard';


function App() {

  // 😀 useState는 페이지 랜더링 시 1회만! 발동! 이후 발동 하지 않는다!!
  const [inputValue,setInputValue] = useState('');    // 아직 입력한 값은 빈 값이니까 비워두기
  const [todoList,setTodoList] = useState([]);    // 아이템을 모아두려면 어떤 타입이 제일 좋으냐 배열이 좋다.

  const addItem = () => {
    console.log("추가중..");
    setTodoList([...todoList, inputValue])  // 기존에 있는 아이템 유지하고 새로추가
    console.log(todoList);
  }

  const DeleteClick = (delete_item) => {
    setTodoList(todoList.filter(item=>item!==delete_item));
    console.log(delete_item)
    console.log("삭제중...")
  }


  return (
    <main>
      <input type="text" onChange={(텍스트input)=>{setInputValue(텍스트input.target.value)}}></input>
      <button onClick={addItem}>추가</button>

      <TodoBoard key={1} delete={DeleteClick} todoList={todoList}/>
    </main>
  );
}

export default App;
