import logo from './logo.svg';
import './App.css';
import React, { useState } from 'react';
import TodoBoard from './components/TodoBoard';


function App() {

  const [inputValue,setInputValue] = useState('');    // 아직 입력한 값은 빈 값이니까 비워두기
  const [todoList,setTodoList] = useState([]);    // 아이템을 모아두려면 어떤 타입이 제일 좋으냐 배열이 좋다.

  const addItem = () => {
    setTodoList([...todoList, inputValue])  // 기존에 있는 아이템 유지하고 새로추가
  }

  const DeleteClick = (item) => {
    todoList.filter((item1)=>{item1!==item})
  }


  return (
    <main>
      <input value={inputValue} type="text" onChange={(event)=>setInputValue(event.target.value)}></input>
      <button onClick={addItem}>추가</button>

      <TodoBoard onClick={DeleteClick} todoList={todoList}/>
    </main>
  );
}

export default App;
